import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Dashboard = () => {
  const [role, setRole] = useState(null);
  const [userInfo, setUserInfo] = useState({});
  const [loading, setLoading] = useState(true);
  const [allUsers, setAllUsers] = useState([]); // State to store fetched users
  const [fetchingUsers, setFetchingUsers] = useState(false); // Loading state for fetching users
  const [error, setError] = useState(null); // State to handle errors

  useEffect(() => {
    const fetchUserInfo = async () => {
      const token = localStorage.getItem('token');

      if (!token) {
        console.log('No token found, redirecting to login');
        window.location.href = '/'; // Redirect if no token is found
        return;
      }

      try {
        const response = await axios.get('http://localhost:4000/api/users/profile', {
          headers: { Authorization: `Bearer ${token}` },
        });

        // Set user info and role once the API call is successful
        setUserInfo(response.data);
        setRole(response.data.role); // Set the role from the response
        setLoading(false); // Stop loading once data is fetched
      } catch (err) {
        console.error('Error fetching user data:', err);
        window.location.href = '/'; // Redirect if there's an error
      }
    };

    fetchUserInfo();
  }, []);

  // Function to fetch all users for admin role
  const fetchAllUsers = async () => {
    const token = localStorage.getItem('token');

    if (!token) {
      console.log('No token found, redirecting to login');
      window.location.href = '/'; // Redirect if no token is found
      return;
    }

    setFetchingUsers(true); // Set loading state for fetching users

    try {
      const response = await axios.get('http://localhost:4000/api/users/admin', {
        headers: { Authorization: `Bearer ${token}` },
      });

      setAllUsers(response.data); // Set the fetched users
      setFetchingUsers(false); // Stop loading
    } catch (err) {
      console.error('Error fetching all users:', err);
      setError('Error fetching users. Please try again.');
      setFetchingUsers(false); // Stop loading on error
    }
  };

  if (loading) {
    return <div>Loading...</div>; // Display loading state while fetching user data
  }

  return (
    <div>
      <h1>Dashboard</h1>

      {role === 'admin' && (
        <div>
          <h2>Admin Actions</h2>
          <button onClick={fetchAllUsers} disabled={fetchingUsers}>
            {fetchingUsers ? 'Loading users...' : 'Fetch All Users'}
          </button>
          
          {error && <p style={{ color: 'red' }}>{error}</p>}

          <div>
            <h3>All Users:</h3>
            {allUsers.length === 0 ? (
              <p>No users found.</p>
            ) : (
              <ul>
                {allUsers.map((user) => (
                  <li key={user._id}>
                    {user.name} ({user.email})
                  </li>
                ))}
              </ul>
            )}
          </div>
        </div>
      )}

      {role === 'user' && (
        <div>
          <h2>Your Profile</h2>
          <p>Name: {userInfo.name}</p>
          <p>Email: {userInfo.email}</p>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
