import React, { useState } from 'react';
import axios from 'axios';
import loginImage from './assets/login.png'; // Adjust the import path if needed
import './login.css'; // Import the CSS file

const Login = () => {
  // States for form fields
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [message, setMessage] = useState('');

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Clear previous messages
      setError('');
      setMessage('');

      // Make API request to login endpoint
      const response = await axios.post(
        'http://localhost:4000/api/users/login',
        { email, password },
        {
          withCredentials: true,
        }
      );

      // Handle success
      if (response.status === 200) {
        setMessage(response.data.message);
        setEmail('');
        setPassword('');
        alert('Login successful!'); // Using alert for simplicity
      }
    } catch (error) {
      // Handle error
      if (error.response) {
        setError(error.response.data.message); // Error from backend
        alert(error.response.data.message); // Using alert for simplicity
      } else {
        setError('An error occurred. Please try again later.');
        alert('An error occurred. Please try again later.');
      }
    }
  };

  return (
    <div className="login-container">
      <div className="login-box">
        {/* Left side: Image */}
        <div className="image-container">
          <img src={loginImage} alt="Login Illustration" />
        </div>

        {/* Right side: Login Form */}
        <div className="form-container">
          <h2>Login</h2>
          {error && <p className="error-message">{error}</p>}
          {message && <p className="success-message">{message}</p>}

          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <label htmlFor="email">Email</label>
              <input
                type="email"
                id="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                placeholder="Enter your email"
                required
              />
            </div>

            <div className="form-group">
              <label htmlFor="password">Password</label>
              <input
                type="password"
                id="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                placeholder="Enter your password"
                required
              />
            </div>

            <button type="submit" className="submit-btn">
              Login
            </button>

            <p className="signup-text">
              Don't have an account? <a href="/register">Sign up</a>
            </p>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Login;
