import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Profile = () => {
  const [userInfo, setUserInfo] = useState({ email: '', name: '' });
  const [loading, setLoading] = useState(true); // Loading state for better UX
  const [error, setError] = useState(''); // Error state for display purposes

  useEffect(() => {
    const fetchUserInfo = async () => {
      const token = localStorage.getItem('token');

      if (!token) {
        console.error('No token found. Redirecting to login.');
        window.location.href = '/'; // Redirect to login page
        return;
      }

      try {
        // Make API call to fetch user info
        const response = await axios.get('http://localhost:4000/api/users', {
          headers: { Authorization: `Bearer ${token}` },
        });

        // Update user info in the state
        setUserInfo({
          email: response.data.email,
          name: response.data.name,
        });

        setLoading(false); // Mark as finished loading
      } catch (err) {
        console.error(
          'Error fetching user info:',
          err.response?.data?.message || err.message
        );
        setError('Failed to fetch user information. Please log in again.');
        setLoading(false);

        // Redirect to login page after a short delay
        setTimeout(() => {
          window.location.href = '/';
        }, 3000);
      }
    };

    fetchUserInfo();
  }, []);

  if (loading) {
    return <div>Loading...</div>; // Display a loading message or spinner
  }

  if (error) {
    return (
      <div>
        <p>{error}</p>
      </div>
    ); // Display error message if there's an error
  }

  return (
    <div>
      <nav>
        <ul>
          <li>Welcome, {userInfo.name}</li>
          <li>Email: {userInfo.email}</li>
        </ul>
      </nav>

      <h1>Your Profile</h1>
      <p>Here you can view and manage your account details.</p>
    </div>
  );
};

export default Profile;
