import React, { useState } from 'react';
import axios from 'axios';
import './login.css';
import { FaEnvelope, FaLock } from 'react-icons/fa'; // Importing specific icons from React Icons
import login from './login.png';
import { useNavigate } from 'react-router-dom';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [message, setMessage] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      setError('');
      setMessage('');

      const response = await axios.post(
        'http://localhost:4000/api/users/login',
        { email, password },
        { withCredentials: true }
      );

      if (response.status === 200) {
        setMessage(response.data.message);
        setEmail('');
        setPassword('');
        // alert('Login successful!');
        localStorage.setItem('token', response.data.token); // Assuming response contains a 'token'
      
        alert('Login successful!');
        navigate('/Dashboard'); // Navigate to the profile page
      }
    //   localStorage.setItem('token', response.data.token); // Assuming response contains a 'token'

    } catch (error) {
      if (error.response) {
        setError(error.response.data.message);
        alert(error.response.data.message);
      } else {
        setError('An error occurred. Please try again later.');
        alert('An error occurred. Please try again later.');
      }
    }
  };

  return (
    <div className="login-container">
      <div className="login-card">
        <div className="login-image">
          <img src={login} alt="Welcome" />
        </div>

        <div className="login-form">
          <div className="login-header">
            <h2>Welcome</h2>
            <p>Login to your account</p>
          </div>
          <form onSubmit={handleSubmit}>
            {error && <p className="error-message">{error}</p>}
            {message && <p className="success-message">{message}</p>}

            <div className="form-group">
           
              <div className="input-wrapper">
                <FaEnvelope className="input-icon" /> 
                <input
                  type="email"
                  id="email"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  placeholder="Enter your email"
                  required
                />
              </div>
            </div>

            <div className="form-group">
             
              <div className="input-wrapper">
                <FaLock className="input-icon" /> {/* Password icon */}
                <input
                  type="password"
                  id="password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  placeholder="Enter your password"
                  required
                />
              </div>
            </div>

            <button type="submit" className="submit-btn">
              Login
            </button>

            <p className="signup-text">
              Don't have an account? <a href="/register">Sign up</a>
            </p>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Login;
