import React, { useState } from 'react';
import axios from 'axios';
import {
  Box,
  Button,
  FormControl,
  FormLabel,
  Input,
  Heading,
  Text,
 
  Stack,
  Flex,
  Image,
} from '@chakra-ui/react';
import { useToast } from '@chakra-ui/react';

const Login = () => {
  // States for form fields
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [message, setMessage] = useState('');

  const toast = useToast();  // Chakra UI Toast for error/success messages

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Clear previous messages
      setError('');
      setMessage('');

      // Make API request to login endpoint
      const response = await axios.post(
        'http://localhost:4000/api/users/login',
        { email, password },
        {
          withCredentials: true,
        }
      );

      // Handle success
      if (response.status === 200) {
        setMessage(response.data.message);
        setEmail('');
        setPassword('');
        toast({
          title: 'Login successful!',
          description: response.data.message,
          status: 'success',
          duration: 5000,
          isClosable: true,
        });
      }
    } catch (error) {
      // Handle error
      if (error.response) {
        setError(error.response.data.message); // Error from backend
        toast({
          title: 'Error',
          description: error.response.data.message,
          status: 'error',
          duration: 5000,
          isClosable: true,
        });
      } else {
        setError('An error occurred. Please try again later.');
        toast({
          title: 'Error',
          description: 'An error occurred. Please try again later.',
          status: 'error',
          duration: 5000,
          isClosable: true,
        });
      }
    }
  };

  return (
    <Flex minHeight="100vh" align="center" justify="center">
      <Box
        maxWidth="lg"
        width="full"
        p={6}
        borderRadius="md"
        boxShadow="md"
        bg="white"
        display="flex"
        flexDirection={{ base: 'column', lg: 'row' }}
        alignItems="center"
        justifyContent="center"
      >
        {/* Left side: Image */}
        <Box flex="1" display={{ base: 'none', lg: 'block' }} paddingRight="6">
          <Image
            src="https://via.placeholder.com/400x300" // Replace with your image URL
            alt="Login Illustration"
            borderRadius="md"
            boxShadow="lg"
          />
        </Box>

        {/* Right side: Login Form */}
        <Box flex="2" width="100%">
          <Heading as="h2" size="lg" mb={4} textAlign="center">
            Login
          </Heading>
          {error && <Text color="red.500" mb={4}>{error}</Text>}
          {message && <Text color="green.500" mb={4}>{message}</Text>}

          <form onSubmit={handleSubmit}>
            <Stack spacing={4}>
              {/* Email Field */}
              <FormControl isRequired>
                <FormLabel htmlFor="email">Email</FormLabel>
                <Input
                  type="email"
                  id="email"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  placeholder="Enter your email"
                  size="lg"
                />
              </FormControl>

              {/* Password Field */}
              <FormControl isRequired>
                <FormLabel htmlFor="password">Password</FormLabel>
                <Input
                  type="password"
                  id="password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  placeholder="Enter your password"
                  size="lg"
                />
              </FormControl>

              {/* Submit Button */}
              <Button
                type="submit"
                colorScheme="teal"
                size="lg"
                width="full"
                mt={4}
                isLoading={false}
              >
                Login
              </Button>

              <Text textAlign="center" mt={4}>
                Don't have an account? <a href="/register">Sign up</a>
              </Text>
            </Stack>
          </form>
        </Box>
      </Box>
    </Flex>
  );
};

export default Login;
