import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Dashboard.css';

const Dashboard = () => {
  const [role, setRole] = useState(null);
  const [userInfo, setUserInfo] = useState({});
  const [loading, setLoading] = useState(true);
  const [allUsers, setAllUsers] = useState([]);
  const [fetchingUsers, setFetchingUsers] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchUserInfo = async () => {
      const token = localStorage.getItem('token');

      if (!token) {
        console.log('No token found, redirecting to login');
        window.location.href = '/';
        return;
      }

      try {
        const response = await axios.get('http://localhost:4000/api/users/profile', {
          headers: { Authorization: `Bearer ${token}` },
        });

        setUserInfo(response.data);
        setRole(response.data.role);
        setLoading(false);
      } catch (err) {
        console.error('Error fetching user data:', err);
        window.location.href = '/';
      }
    };

    fetchUserInfo();
  }, []);

  const fetchAllUsers = async () => {
    const token = localStorage.getItem('token');

    if (!token) {
      console.log('No token found, redirecting to login');
      window.location.href = '/';
      return;
    }

    setFetchingUsers(true);

    try {
      const response = await axios.get('http://localhost:4000/api/users/admin', {
        headers: { Authorization: `Bearer ${token}` },
      });

      setAllUsers(response.data);
      setFetchingUsers(false);
    } catch (err) {
      console.error('Error fetching all users:', err);
      setError('Error fetching users. Please try again.');
      setFetchingUsers(false);
    }
  };

  if (loading) {
    return <div className="loading">Loading...</div>;
  }

  return (
    <div className="dashboard">
      {/* Sidebar */}
      <aside className="sidebar">
        <h1>Admin Panel</h1>
        <ul>
          <li className="active">Dashboard</li>
          <li>Users</li>
          <li>Projects</li>
          <li>Settings</li>
        </ul>
      </aside>

      {/* Main Content */}
      <main className="content">
        <header>
          <h2>Dashboard</h2>
        </header>

        {role === 'admin' && (
          <div className="admin-section">
            <h3>Admin Actions</h3>
            <button className="btn-fetch" onClick={fetchAllUsers} disabled={fetchingUsers}>
              {fetchingUsers ? 'Loading users...' : 'Fetch All Users'}
            </button>
            {error && <p className="error">{error}</p>}

            <div className="users-list">
              <h3>All Users:</h3>
              {allUsers.length === 0 ? (
                <p>No users found.</p>
              ) : (
                <table>
                  <thead>
                    <tr>
                      <th>Name</th>
                      <th>Email</th>
                      <th>Actions</th>
                    </tr>
                  </thead>
                  <tbody>
                    {allUsers.map((user) => (
                      <tr key={user._id}>
                        <td>{user.fullnamename}</td>
                        <td>{user.email}</td>
                        <td>
                          <button className="btn-profile">See Profile</button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              )}
            </div>
          </div>
        )}

        {role === 'user' && (
          <div className="profile-section">
            <h3>Your Profile</h3>
            <p>Name: {userInfo.name}</p>
            <p>Email: {userInfo.email}</p>
          </div>
        )}
      </main>
    </div>
  );
};

export default Dashboard;
