import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Dashboard = () => {
  const [role, setRole] = useState(null);
  const [userInfo, setUserInfo] = useState({});
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchUserInfo = async () => {
      const token = localStorage.getItem('token'); // Get token from localStorage

      if (!token) {
        console.log('No token found, redirecting to login');
        window.location.href = '/'; // Redirect if no token is found
        return;
      }

      try {
        const response = await axios.get('http://localhost:4000/api/users/profile', {
          headers: { Authorization: `Bearer ${token}` },
        });

        // Set user info and role once the API call is successful
        setUserInfo(response.data);
        setRole(response.data.role); // Set the role from the response
        setLoading(false); // Stop loading once data is fetched
      } catch (err) {
        console.error('Error fetching user data:', err);
        window.location.href = '/'; // Redirect if there's an error
      }
    };

    fetchUserInfo();
  }, []);

  const fetchAllUsers = async () => {
    const token = localStorage.getItem('token');
    try {
      const response = await axios.get('http://localhost:4000/api/users/admin', {
        headers: { Authorization: `Bearer ${token}` },
      });
      console.log(response.data); // Log all users (only for admins)
    } catch (err) {
      console.error('Error fetching users:', err);
    }
  };

//   const updateUserRole = async (userId, role) => {
//     const token = localStorage.getItem('token');
//     try {
//       const response = await axios.put(`http://localhost:4000/api/users/admin/users/${userId}/role`, 
//         { role },
//         { headers: { Authorization: `Bearer ${token}` } }
//       );
//       console.log('Role updated:', response.data);
//     } catch (err) {
//       console.error('Error updating user role:', err);
//     }
//   };

  if (loading) {
    return <div>Loading...</div>; // Display loading state while fetching data
  }

  return (
    <div>
      <h1>Dashboard</h1>

      {role === 'admin' && (
        <div>
          <h2>Admin Actions</h2>
          <button onClick={fetchAllUsers}>Fetch All Users</button>
          {/* <button onClick={() => updateUserRole('user-id-here', 'user')}>Update User Role</button> */}
        </div>
      )}

      {role === 'user' && (
        <div>
          <h2>Your Profile</h2>
          <p>Name: {userInfo.name}</p>
          <p>Email: {userInfo.email}</p>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
