import React, { useState } from 'react';
import axios from 'axios'; // Import axios
import { Box, Button, FormControl, FormLabel, Input, useToast } from '@chakra-ui/react';

const Register = () => {
  const [fullname, setFullname] = useState('');
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');  // Add a state for the message
  const toast = useToast();

  const handleSubmit = async (event) => {
    event.preventDefault();
    setLoading(true);

    try {
      const response = await axios.post('/register', {
        fullname,
        username,
        email,
        password,
      });

      // Handle success message
      setMessage(response.data.message);
      toast({
        title: "Registration Successful",
        description: response.data.message,
        status: "success",
        duration: 5000,
        isClosable: true,
      });
      // You can clear the form or redirect user if needed
    } catch (error) {
      setMessage(error.response?.data?.message || "Something went wrong");
      toast({
        title: "Error",
        description: error.response?.data?.message || "An error occurred",
        status: "error",
        duration: 5000,
        isClosable: true,
      });
    } finally {
      setLoading(false);
    }
  };

  return (
    <Box maxWidth="400px" mx="auto" mt="50px" p="5" borderRadius="md" boxShadow="lg">
      <form onSubmit={handleSubmit}>
        <FormControl mb="4">
          <FormLabel>Full Name</FormLabel>
          <Input type="text" value={fullname} onChange={(e) => setFullname(e.target.value)} required />
        </FormControl>
        <FormControl mb="4">
          <FormLabel>Username</FormLabel>
          <Input type="text" value={username} onChange={(e) => setUsername(e.target.value)} required />
        </FormControl>
        <FormControl mb="4">
          <FormLabel>Email</FormLabel>
          <Input type="email" value={email} onChange={(e) => setEmail(e.target.value)} required />
        </FormControl>
        <FormControl mb="4">
          <FormLabel>Password</FormLabel>
          <Input type="password" value={password} onChange={(e) => setPassword(e.target.value)} required />
        </FormControl>
        <Button colorScheme="teal" width="full" isLoading={loading} type="submit">
          Register
        </Button>
      </form>
      {message && <Box mt="4" color={message.startsWith("Error") ? "red.500" : "green.500"}>{message}</Box>}
    </Box>
  );
};

export default Register;
