import React, { useContext } from 'react';
import { BrowserRouter as Router, Route, Switch, Redirect } from 'react-router-dom';
import AuthContext from './context/AuthContext';  // Import AuthContext
import ProtectedRoute from './components/ProtectedRoute.js';  // Import ProtectedRoute
import Login from './Login.js';  // Login Page
import Register from './Register.js';  // Register Page
import Dashboard from './pages/Dashboard.js';  // Protected Page
import AdminPanel from './pages/AdminPanel.js';  // Admin-only page (if needed)

function App() {
  const { user } = useContext(AuthContext);  // Get the user from AuthContext

  return (
    <Router>

        {/* Public Routes */}
        <Route path="/login" component={Login} />
        <Route path="/register" component={Register} />

        {/* Protected Routes */}
        <ProtectedRoute path="/dashboard" component={Dashboard} />
        <ProtectedRoute path="/admin" component={AdminPanel} />

        {/* Redirect to login if route doesn't match */}
        <Redirect to="/login" />

    </Router>
  );
}

export default App;
