import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './Registertion.css'; 
import { FaUser, FaUserCircle, FaEnvelope, FaLock, FaEye, FaEyeSlash,FaUserFriends, FaUserTie } from 'react-icons/fa'; 
import registerImage from './login.png'; 

const Register = () => {
  const [fullname, setFullname] = useState('');
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [gender, setGender] = useState('');

  const [role, setRole] = useState(''); 
  const [showPassword, setShowPassword] = useState(false);
  const [loading, setLoading] = useState(false);
  const [passwordError, setPasswordError] = useState('');
  const [message, setMessage] = useState('');
  const navigate = useNavigate(); 

  const passwordRegex = /^(?=.*[A-Z])(?=.*[!@#$%^&*])(?=.*[0-9])(?=.*[a-z]).{8,}$/;

  const handlePasswordChange = (e) => {
    const value = e.target.value;
    setPassword(value);

    if (!passwordRegex.test(value)) {
      setPasswordError(
        'Password must be at least 8 characters long, include an uppercase letter, a number, and a special character.'
      );
    } else {
      setPasswordError('');
    }
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    if (!passwordRegex.test(password)) {
      setPasswordError(
        'Password must be at least 8 characters long, include an uppercase letter, a number, and a special character.'
      );
      alert('Please ensure the password meets the requirements.');
      return; 
    }
    if (password.trim() !== confirmPassword.trim()) {
      alert('Passwords do not match.');
      return; 
    }

    setLoading(true); 
    setPasswordError(''); 

    try {
      const response = await axios.post('http://localhost:4000/api/users/register', {
        fullname,
        username,
        email,
        password,
        confirmPassword,
        role, 
        gender
      });

      setMessage(response.data.message);
      alert('Registration Successful: ' + response.data.message);
      navigate('/');
    } catch (error) {
      setMessage(error.response?.data?.message || 'Something went wrong');
      alert('Error: ' + (error.response?.data?.message || 'An error occurred'));
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="register-container">
      <div className="register-card">
        <div className="register-image">
          <img src={registerImage} alt="Welcome" />
        </div>

        <form onSubmit={handleSubmit} className="register-form">
          <div className="register-header">
            <h2>Create Account</h2>
            <p>Sign up to get started</p>
          </div>

          {/* Full Name */}
          <div className="form-control">
            <div className="input-wrapper">
              <FaUserCircle className="input-icon" />
              <input
                type="text"
                id="fullname"
                value={fullname}
                onChange={(e) => setFullname(e.target.value)}
                placeholder="Enter your full name"
                required
              />
            </div>
          </div>

        
          <div className="form-control">
            <div className="input-wrapper">
              <FaUser className="input-icon" />
              <input
                type="text"
                id="username"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                placeholder="Enter your username"
                required
              />
            </div>
          </div>

    
          <div className="form-control">
            <div className="input-wrapper">
              <FaEnvelope className="input-icon" />
              <input
                type="email"
                id="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                placeholder="Enter your email"
                required
              />
            </div>
          </div>

         
          <div className="form-control">
            <div className="input-wrapper">
              <FaLock className="input-icon" />
              <input
                type={showPassword ? 'text' : 'password'}
                id="password"
                value={password}
                onChange={handlePasswordChange}
                placeholder="Enter your password"
                required
              />
              <span
                className="toggle-password"
                onClick={() => setShowPassword((prev) => !prev)}
              >
                {showPassword ? <FaEyeSlash /> : <FaEye />}
              </span>
            </div>
          </div>

         
          <div className="form-control">
            <div className="input-wrapper">
              <FaLock className="input-icon" />
              <input
                type={showPassword ? 'text' : 'password'}
                id="confirmPassword"
                value={confirmPassword}
                onChange={(e) => setConfirmPassword(e.target.value)}
                placeholder="Confirm your password"
                required
              />
              <span
                className="toggle-password"
                onClick={() => setShowPassword((prev) => !prev)}
              >
                {showPassword ? <FaEyeSlash /> : <FaEye />}
              </span>
            </div> 
          </div>
          <div className="form-control">
  <div className="input-wrapper">
    <FaUserFriends className="input-icon" />
    <select
      id="gender"
      value={gender}
      onChange={(e) => setGender(e.target.value)}
      required
    >
      <option value="" disabled>Select your gender</option>
      <option value="male">Male</option>
      <option value="female">female</option>
 
    </select>
  </div>
</div>

          
          <div className="form-control">
  <div className="input-wrapper">
    <FaUserTie className="input-icon" />
    <select
      id="role"
      value={role}
      onChange={(e) => setRole(e.target.value)}
      required
    >
      <option value="" disabled>Select your role</option>
      <option value="admin">Admin</option>
      <option value="user">User</option>
      <option value="moderator">Moderator</option>
      <option value="guest">Guest</option>
    </select>
  </div>
</div>


          <button type="submit" className="submit-btn" disabled={loading}>
            {loading ? 'Registering...' : 'Register'}
          </button>
        </form>
      </div>

      
      {message && (
        <div className={message.startsWith('Error') ? 'error' : 'success'}>
          {message}
        </div>
      )}
    </div>
  );
};

export default Register;
