import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Dashboard = () => {
  const [role, setRole] = useState(null);
  const [userInfo, setUserInfo] = useState({});
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchUserInfo = async () => {
      const token = localStorage.getItem('token'); // Retrieve token from localStorage

      if (!token) {
        console.log('No token found, redirecting to login');
        window.location.href = '/'; // Redirect if no token is found
        return;
      }

      try {
        const response = await axios.get('http://localhost:4000/api/users/profile', {
          headers: { Authorization: `Bearer ${token}` },
        });

        // Set user info and role once the API call is successful
        setUserInfo(response.data);
        setRole(response.data.role); // Set the role from the response
        setLoading(false); // Stop loading once data is fetched
      } catch (err) {
        console.error('Error fetching user data:', err);
        window.location.href = '/'; // Redirect if there's an error
      }
    };

    fetchUserInfo();
  }, []);

  if (loading) {
    return <div>Loading...</div>; // Display loading state while fetching data
  }

  return (
    <div>
      <h1>Dashboard</h1>

      {role === 'admin' && (
        <div>
          <h2>Admin Actions</h2>
          <button onClick={() => alert('Fetching All Users')}>Fetch All Users</button>
        </div>
      )}

      {role === 'user' && (
        <div>
          <h2>Your Profile</h2>
          <p>Name: {userInfo.name}</p>
          <p>Email: {userInfo.email}</p>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
